package commimport Depsimport TestDepsimport Versionsimport extensions.androidTestImplementationimport extensions.debugImplementationimport extensions.implementationimport extensions.testImplementation/** * 作者：hsicen  11/3/21 10:17 * 邮箱：codinghuang@163.com * 功能： * 描述：android library module comm configuration with compose */plugins {  id("com.android.library")  id("kotlin-android")}android {  compileSdk = Versions.compileSdk  defaultConfig {    minSdk = Versions.minSdk    targetSdk = Versions.targetSdk    testInstrumentationRunner = TestDeps.runner    vectorDrawables {      useSupportLibrary = true    }  }  buildTypes {    getByName("release") {      isMinifyEnabled = false      proguardFiles(        getDefaultProguardFile("proguard-android-optimize.txt"),        "proguard-rules.pro"      )    }  }  compileOptions {    sourceCompatibility = JavaVersion.VERSION_17    targetCompatibility = JavaVersion.VERSION_17  }  kotlinOptions {    jvmTarget = "17"  }  buildFeatures {    compose = true  }  composeOptions {    kotlinCompilerExtensionVersion = Versions.composeCompiler  }  packaging {    resources {      excludes.add("/META-INF/{AL2.0,LGPL2.1}")    }  }}dependencies {  testImplementation(TestDeps.junit)  androidTestImplementation(TestDeps.junitExt)  androidTestImplementation(TestDeps.espresso)  implementation(Deps.ktx)  implementation(Deps.appCompat)  implementation(Deps.material)  implementation(Deps.lifecycleRuntimeKtx)  implementation(Deps.activityCompose)  implementation(Deps.composeMaterial3)  androidTestImplementation(TestDeps.composeUiTest)  debugImplementation(TestDeps.composeUiTooling)}