package extensionsimport org.gradle.api.Actionimport org.gradle.api.Projectimport org.gradle.api.artifacts.Dependencyimport org.gradle.api.artifacts.dsl.DependencyHandlerimport org.gradle.api.plugins.JavaPluginExtensionimport org.gradle.kotlin.dsl.add/** * 作者：hsicen  11/2/21 18:05 * 邮箱：codinghuang@163.com * 功能： * 描述：DependencuHandler  Extension */fun DependencyHandler.kapt(list: List<String>) {  list.forEach { dependency ->    add("kapt", dependency)  }}fun DependencyHandler.implementation(list: List<String>) {  list.forEach { dependency ->    add("implementation", dependency)  }}fun DependencyHandler.androidTestImplementation(list: List<String>) {  list.forEach { dependency ->    add("androidTestImplementation", dependency)  }}fun DependencyHandler.testImplementation(list: List<String>) {  list.forEach { dependency ->    add("testImplementation", dependency)  }}/***======copy from ImplementationConfigurationAccessors======****//** * Adds a dependency to the `debugImplementation` configuration. * * @param dependencyNotation name of dependency to add at specific configuration * * @return the dependency */fun DependencyHandler.debugImplementation(dependencyNotation: String): Dependency? =  add("debugImplementation", dependencyNotation)/** * Adds a dependency to the `implementation` configuration. * * @param dependencyNotation name of dependency to add at specific configuration * * @return the dependency */fun DependencyHandler.implementation(dependencyNotation: String): Dependency? =  add("implementation", dependencyNotation)/** * Adds a dependency to the `api` configuration. * * @param dependencyNotation name of dependency to add at specific configuration * * @return the dependency */fun DependencyHandler.api(dependencyNotation: String): Dependency? =  add("api", dependencyNotation)/** * Adds a dependency to the `kapt` configuration. * * @param dependencyNotation name of dependency to add at specific configuration * * @return the dependency */fun DependencyHandler.kapt(dependencyNotation: String): Dependency? =  add("kapt", dependencyNotation)/** * Adds a dependency to the `testImplementation` configuration. * * @param dependencyNotation name of dependency to add at specific configuration * * @return the dependency */fun DependencyHandler.testImplementation(dependencyNotation: String): Dependency? =  add("testImplementation", dependencyNotation)/** * Adds a dependency to the `androidTestImplementation` configuration. * * @param dependencyNotation name of dependency to add at specific configuration * * @return the dependency */fun DependencyHandler.androidTestImplementation(dependencyNotation: String): Dependency? =  add("androidTestImplementation", dependencyNotation)/** * Adds a dependency to the 'implementation' configuration. * * @param dependencyNotation notation for the dependency to be added. * @return The dependency. * * @see [DependencyHandler.add] */fun DependencyHandler.implementation(dependencyNotation: Any): Dependency? =  add("implementation", dependencyNotation)/** * Configures the [java][org.gradle.api.plugins.JavaPluginExtension] extension. */fun Project.java(configure: Action<JavaPluginExtension>): Unit =  (this as org.gradle.api.plugins.ExtensionAware).extensions.configure("java", configure)