package com.hsicen.hellocompose.uiimport androidx.compose.foundation.layout.Columnimport androidx.compose.foundation.layout.fillMaxSizeimport androidx.compose.foundation.pager.HorizontalPagerimport androidx.compose.foundation.pager.rememberPagerStateimport androidx.compose.runtime.Composableimport androidx.compose.runtime.rememberCoroutineScopeimport androidx.compose.ui.Modifierimport com.hsicen.hellocompose.WeViewModelimport kotlinx.coroutines.launch/** * 作者：hsicen  12/10/21 23:17 * 邮箱：codinghuang@163.com * 作用： * 描述：HencoderPlus */@Composablefun Home(viewModel: WeViewModel) {  Column(modifier = Modifier.fillMaxSize()) {    // PagerState 现在需要 pageCount 参数    val pagerState = rememberPagerState(initialPage = 0) {      4 // 总页数    }    HorizontalPager(      state = pagerState,      modifier = Modifier.weight(1f)    ) { pos ->      when (pos) {        0 -> ChatListPage(viewModel.chats)        1 -> ContactPage(viewModel)        2 -> DiscoveryPage()        3 -> MePage()      }    }    val scope = rememberCoroutineScope()    WeBottomBar(      select = pagerState.currentPage,      onSelect = { page ->        scope.launch {          pagerState.animateScrollToPage(page)        }      }    )  }}