package com.hsicen.hellocompose.uiimport androidx.compose.foundation.layout.Columnimport androidx.compose.runtime.Composableimport androidx.compose.runtime.rememberCoroutineScopeimport androidx.compose.ui.Modifierimport com.google.accompanist.pager.ExperimentalPagerApiimport com.google.accompanist.pager.HorizontalPagerimport com.google.accompanist.pager.rememberPagerStateimport com.hsicen.hellocompose.WeViewModelimport kotlinx.coroutines.launch/** * 作者：hsicen  12/10/21 23:17 * 邮箱：codinghuang@163.com * 作用： * 描述：HencoderPlus */@ExperimentalPagerApi@Composablefun Home(viewModel: WeViewModel) {  Column {    val pagerState = rememberPagerState()    HorizontalPager(count = 4, Modifier.weight(1f), pagerState) { pos ->      when (pos) {        0 -> ChatListPage(viewModel.chats)        1 -> ContactPage(viewModel)        2 -> DiscoveryPage()        3 -> MePage()      }    }    val scope = rememberCoroutineScope()    WeBottomBar(pagerState.currentPage, onSelect = { page ->      scope.launch {        pagerState.animateScrollToPage(page)      }    })  }}