package com.hsicen.a05_modifierimport androidx.activity.ComponentActivityimport androidx.activity.compose.setContentimport androidx.compose.foundation.layout.Rowimport androidx.compose.material3.Textimport androidx.compose.runtime.Composableimport androidx.compose.ui.Modifierimport androidx.compose.ui.layout.LookaheadScopeimport androidx.compose.ui.layout.onPlacedimport androidx.compose.ui.layout.positionInParent/******====== 49. LookaheadOnPlacedModifier 的作用、写法和原理 ======******//** * 作用： *  在 LookaheadLayout() ⾥充当功能更强的 OnPlacedModifier 存在，可以额外提供 lookahead （前瞻性预测量和布局）阶段得到的位置和尺⼨ * * 写法：[ModifierLookaheadOnPlaced01] * * 原理： *  1.和 OnPlacedModifer 一样 *  2.会在回调时，额外取一下之前测量好的 lookahead 的 LayoutCoodinates 信息，来交给回调函数 */fun ComponentActivity.composeModifier12() {  setContent {    ModifierLookaheadOnPlaced01()  }}@Composablefun ModifierLookaheadOnPlaced01() {  LookaheadScope {    Row(Modifier.onPlaced {      val pos = it.positionInParent()      val size = it.size    }) {      Text(text = "Hello Compose!")    }  }}