package com.hsicen.a06_coroutineimport androidx.activity.ComponentActivityimport androidx.activity.compose.setContentimport androidx.compose.foundation.clickableimport androidx.compose.foundation.layout.Boximport androidx.compose.foundation.layout.paddingimport androidx.compose.material.Textimport androidx.compose.runtime.*import androidx.compose.ui.Modifierimport androidx.compose.ui.unit.dpimport androidx.compose.ui.unit.sp/******====== 59.snapshotFlow()：把 Compose 的 State 转换成协程 Flow ======******//** * snapshotFlow() 的作⽤和效果： *  1.创建⼀个 Flow ，这个 Flow 对⽤到的 Compose 的 State 是有感知的； *  2.当「⽤到的 Flow ⾥会⽤到 Compose 的 State 」的时候，就⽤它； *  3.不要考虑它和「从协程以及其他状态向 Compose 的状态转换」的使⽤场景的关系， *    因为它们没关系。 */fun ComponentActivity.composeCoroutine07() {  setContent {    SnapshotFlow01()  }}@Composableprivate fun SnapshotFlow01() {  var name by remember { mutableStateOf("hsicen") }  var age by remember { mutableIntStateOf(18) }  // snapshotFlow will update when State updated.  val flow = snapshotFlow { "$name $age" }  LaunchedEffect(Unit) {    flow.collect { nameAndAge ->      println("@@@ $nameAndAge")    }  }  Box(modifier = Modifier    .padding(16.dp)    .clickable {      age += 1      name = "hsicen2"    }  ) {    Text(      text = "Compose $name",      fontSize = 30.sp    )  }}