package com.hsicen.a07_viewimport androidx.activity.ComponentActivityimport androidx.activity.compose.setContentimport androidx.compose.foundation.layout.Boximport androidx.compose.foundation.layout.paddingimport androidx.compose.material3.Textimport androidx.compose.runtime.Composableimport androidx.compose.ui.Modifierimport androidx.compose.ui.unit.dpimport androidx.compose.ui.unit.sp/******====== 60.⾃定义绘制 ======******//** * 在哪⾥写： *  1.基于已有的组件增加效果 *    ①drawBehind() *    ②drawWithContent() *  2.全新绘制 *    ① Canvas() * * 怎么写： *  1.利⽤ DrawScope 的绘制函数（ drawXxx() ）来绘制 *  2.利⽤ DrawScope 的对应函数来进⾏裁切和⼏何变换 * * 关于 Compose 做不到的事： *  1.例如多维度的三维旋转---下沉到 Compose 的 Canvas 或者原⽣的 Canvas 进⾏绘制。 */fun ComponentActivity.composeView01() {  setContent {    ComposeDraw01()  }}@Composableprivate fun ComposeDraw01() {  Box(    modifier = Modifier      .padding(16.dp)  ) {    Text(      text = "Compose",      fontSize = 30.sp    )  }}