package com.hsicen.a07_viewimport androidx.activity.ComponentActivityimport androidx.activity.compose.setContentimport androidx.compose.runtime.Composableimport androidx.compose.ui.tooling.preview.Preview/******====== 64.自定义触摸和一维滑动监测 ======******//** * Modifier.draggable() *   1.一维滑动监测用的 Modifier *   2.DraggableState 的参数里填写每一个滑动瞬间的算法 *   3.如果操作界面滑动，要修改的是依赖的状态的值，例如 offset() 里所依赖的偏移量 *   4.interactionSource 参数可以用于手指拖移状态的监听 * * Modifier.scrollable() *   1.也是一维滑动监测用的 Modifier ； *   2.基于 draggable() ，但增加了嵌套滑动、惯性滑动和触边效果的支持， *     专注于滑动布局的应用场景，具体的用法和 draggable() 基本一致 */fun ComponentActivity.composeView05() {  setContent {    TouchAndScroll01()  }}@Preview@Composableprivate fun TouchAndScroll01() {}