package com.hsicen.todo.dataimport androidx.compose.runtime.getValueimport androidx.compose.runtime.mutableStateOfimport androidx.compose.runtime.setValueclass TodoUiState(  val todoName: String,  val todoNums: Int,  initialTodos: List<Todo>) {  private val _todos: MutableList<Todo> = initialTodos.toMutableList()  val todos: List<Todo> = _todos  fun addTodo(todo: Todo) {    _todos.add(0, todo)  }}class Todo {  var name: String by mutableStateOf("")  var completed: Boolean by mutableStateOf(false)}