package com.hsicen.a01_introduceimport androidx.lifecycle.ViewModelimport androidx.lifecycle.viewModelScopeimport kotlinx.coroutines.Dispatchersimport kotlinx.coroutines.delayimport kotlinx.coroutines.launchimport kotlinx.coroutines.withContextclass BasicViewModel : ViewModel() {  fun showThread() {    // Dispatchers.Main.immediate    viewModelScope.launch {      println("@@@viewModelScope.launch1: ${Thread.currentThread().name}")      calSuspend()      println("@@@viewModelScope.launch2: ${Thread.currentThread().name}")    }  }  private suspend fun calSuspend() {    withContext(Dispatchers.Default) {      delay(500)      println("@@@viewModelScope.suspend: ${Thread.currentThread().name}")    }  }}