package com.hsicen.a01_introduceimport androidx.activity.ComponentActivityimport androidx.activity.compose.setContentimport androidx.compose.foundation.backgroundimport androidx.compose.foundation.clickableimport androidx.compose.foundation.layout.paddingimport androidx.compose.material.Textimport androidx.compose.runtime.Composableimport androidx.compose.ui.Modifierimport androidx.compose.ui.graphics.Colorimport androidx.compose.ui.unit.dpimport androidx.compose.ui.unit.spimport com.hsicen.a01_introduce.comm.Contributorimport com.hsicen.a01_introduce.comm.githubServiceimport kotlinx.coroutines.CoroutineScopeimport kotlinx.coroutines.Dispatchersimport kotlinx.coroutines.launch/******====== 05.自定义挂起函数 ======******//** * */fun ComponentActivity.basics05() {  setContent {    ShowContent()    coroutineCustomSuspend01()  }}private fun coroutineCustomSuspend01() {  CoroutineScope(Dispatchers.Main.immediate).launch {    println("@@@basics05 (1) " + Thread.currentThread().name)    val contributors = getContributors()    println("@@@basics05 (3) " + Thread.currentThread().name)  }}private suspend fun getContributors(): List<Contributor> {  println("@@@basics05 (2) " + Thread.currentThread().name)  return githubService.contributors("square", "retrofit")}@Composableprivate fun ShowContent() {  Text(    text = "Hello Coroutine 05!",    fontSize = 25.sp,    modifier = Modifier      .padding(16.dp)      .background(Color.Yellow)      .padding(16.dp)      .clickable {        println("@@@ click from mouse.")      }  )}