package com.hsicen.a01_introduceimport androidx.activity.ComponentActivityimport androidx.activity.compose.setContentimport androidx.compose.foundation.backgroundimport androidx.compose.foundation.clickableimport androidx.compose.foundation.layout.paddingimport androidx.compose.material.Textimport androidx.compose.runtime.Composableimport androidx.compose.ui.Modifierimport androidx.compose.ui.graphics.Colorimport androidx.compose.ui.unit.dpimport androidx.compose.ui.unit.spimport com.hsicen.a01_introduce.comm.Contributorimport com.hsicen.a01_introduce.comm.githubServiceimport kotlinx.coroutines.*/******====== 06.消除魔法：挂起函数为什么不卡线程？ ======******//** *  挂起函数内部是通过接口回调的方式来实现的 *  Dispatchers是通过线程池和Handler来实现切线程的 */fun ComponentActivity.basics07() {  setContent {    ShowContent()    coroutineCustomSuspend01()  }}private fun coroutineCustomSuspend01() {  CoroutineScope(Dispatchers.Main.immediate).launch {    val contributors = getContributors()    showContribute(contributors)  }}private suspend fun showContribute(contributors: List<Contributor>) {  withContext(Dispatchers.Main) {    println("Show:$contributors")  }}private suspend fun getContributors(): List<Contributor> {  return githubService.contributors("square", "retrofit")}private suspend fun processData() =  withContext(Dispatchers.Default) {    println("@@@basics05 (4) " + Thread.currentThread().name)    delay(500)    "Processed: data"  }@Composableprivate fun ShowContent() {  Text(    text = "Hello Coroutine 06!",    fontSize = 25.sp,    modifier = Modifier      .padding(16.dp)      .background(Color.Yellow)      .padding(16.dp)      .clickable {        println("@@@ click from mouse.")      }  )}