package com.hsicen.a01_introduceimport androidx.activity.ComponentActivityimport androidx.activity.compose.setContentimport androidx.compose.foundation.backgroundimport androidx.compose.foundation.clickableimport androidx.compose.foundation.layout.paddingimport androidx.compose.material.Textimport androidx.compose.runtime.Composableimport androidx.compose.ui.Modifierimport androidx.compose.ui.graphics.Colorimport androidx.compose.ui.unit.dpimport androidx.compose.ui.unit.spimport com.hsicen.a01_introduce.comm.Contributorimport com.hsicen.a01_introduce.comm.githubServiceimport kotlinx.coroutines.*import java.util.concurrent.Executorsimport kotlin.concurrent.thread/******====== 08.「轻量级线程」的本质：从 delay() 说起 ======******//** */fun ComponentActivity.basics08() {  setContent {    ShowContent()    showCountCoroutine()  }}private fun coroutineCustomSuspend01() {  CoroutineScope(Dispatchers.Main.immediate).launch {    val contributors = getContributors()    showContribute(contributors)  }}private suspend fun showContribute(contributors: List<Contributor>) {  withContext(Dispatchers.Main) {    println("Show:$contributors")  }}private suspend fun getContributors(): List<Contributor> {  return githubService.contributors("square", "retrofit")}private suspend fun processData() =  withContext(Dispatchers.Default) {    println("@@@basics05 (4) " + Thread.currentThread().name)    delay(500)    "Processed: data"  }private fun showCountJavaExecutor() {  val executor = Executors.newSingleThreadExecutor()  repeat(50_000) {    executor.execute {      Thread.sleep(5000L)      println(it)    }  }}private fun showCountJavaThread() {  repeat(50_000) {    thread {      Thread.sleep(5000L)      println(it)    }  }}private fun showCountCoroutine() {  val scope = CoroutineScope(Dispatchers.Default)  repeat(50_000) {    scope.launch {      delay(5000L)      println(it)    }  }}@Composableprivate fun ShowContent() {  Text(    text = "Hello Coroutine 08!",    fontSize = 25.sp,    modifier = Modifier      .padding(16.dp)      .background(Color.Yellow)      .padding(16.dp)      .clickable {        println("@@@ click from mouse.")      }  )}