package com.hsicen.a01_introduce.commimport retrofit2.Retrofitimport retrofit2.adapter.rxjava3.RxJava3CallAdapterFactoryimport retrofit2.converter.gson.GsonConverterFactoryprivate val retrofit =  Retrofit.Builder().baseUrl(GITHUB_BASE_URL)    .addCallAdapterFactory(RxJava3CallAdapterFactory.create())    .addConverterFactory(GsonConverterFactory.create())    .build()val githubService = retrofit.create(GitHubService::class.java)