package com.hsicen.a02_concurrencyimport kotlinx.coroutines.runBlockingimport kotlin.concurrent.thread/******====== 15.线程的强制结束：stop() ======******//** * Thread.stop: 强制结束，不可预期，可能损坏内存中的数据 */fun main() = runBlocking {  val thread = thread {    println("Thread: I'm running in ${Thread.currentThread().name}")    Thread.sleep(300)    println("I'm done.")  }  Thread.sleep(150)  thread.stop()}