package com.hsicen.a03_scopeimport kotlinx.coroutines.*/******====== 26.GlobalScope ======******//** * GlobalScope：没有生命周期的单例协程启动器 *  1.单例的协程，全局生命周期，顶级协程 *  2.GlobalScope启动的协程之间互不关联 *  3.GlobalScope启动的协程没有父Job *  4. */fun main(): Unit = runBlocking {  test1()  delay(3000)}@OptIn(ExperimentalCoroutinesApi::class)private suspend fun test1() {  val parentJob = GlobalScope.launch {    val job = coroutineContext[Job]    println("job=$job")  }  delay(200)  println("parentJob=${parentJob.parent}")}