package com.hsicen.a03_scopeimport kotlinx.coroutines.*import kotlin.coroutines.EmptyCoroutineContext/******====== 29.withContext ======******//** * withContext： *  1.没填参数的withContext功能与coroutineScope一致 *  2.启动串行的协程 *  3.临时切换CoroutineContext */fun main(): Unit = runBlocking {  test1()  delay(3000)}private suspend fun test1() {  val scope = CoroutineScope(EmptyCoroutineContext)  scope.launch {  }  withContext(Dispatchers.Default) {  }  coroutineScope {  }}