package com.hsicen.a03_scopeimport kotlinx.coroutines.*import kotlin.coroutines.EmptyCoroutineContext/******====== 30.CoroutineName ======******//** * CoroutineName： *  1.相当于给线程取名字 *  2.主要用于开发测试调试 */fun main(): Unit = runBlocking {  test1()  delay(3000)}private suspend fun test1() {  val name = CoroutineName("test1")  val scope = CoroutineScope(EmptyCoroutineContext + name)  scope.launch {    println("1Name thread: ${Thread.currentThread().name}")    println("1Name coroutine: ${coroutineContext[CoroutineName]}")  }  withContext(Dispatchers.Default + name) {    println("2Name thread: ${Thread.currentThread().name}")    println("2Name coroutine: ${coroutineContext[CoroutineName]}")  }  coroutineScope {    println("3Name thread: ${Thread.currentThread().name}")    println("3Name coroutine: ${coroutineContext[CoroutineName]}")  }}