package com.hsicen.a22_thread;import android.os.Handler;import android.os.Looper;/** * 作者：hsicen  8/26/21 22:39 * 邮箱：codinghuang@163.com * 作用： * 描述：Android Test */public class AndroidTest {    private final Handler mHandler;    {        Looper.prepare();        mHandler = new Handler();        Looper.loop();    }    public static void main(String[] args) {        AndroidTest androidTest = new AndroidTest();        androidTest.sendToMain();    }    public void sendToMain() {        System.out.println("1 from thread: " + Thread.currentThread().getName());        new Thread(new Runnable() {            @Override            public void run() {                System.out.println("2 from thread: " + Thread.currentThread().getName());                mHandler.post(new Runnable() {                    @Override                    public void run() {                        System.out.println("3 from thread: " + Thread.currentThread().getName());                    }                });            }        }).start();    }}