package com.hsicen.a22_thread;import java.util.concurrent.locks.LockSupport;/** * 作者：hsicen  8/24/21 17:07 * 邮箱：codinghuang@163.com * 作用： * 描述：HencoderPlus */public class ThreadTest {    Thread thread = new Thread(ThreadTest::threadName);    public static void threadName() {        System.out.println(Thread.currentThread().getName() + " is running.");    }    public static void main(String[] args) {        threadName();        ThreadTest testThread = new ThreadTest();        testThread.thread.start();        LockSupport.park();        LockSupport.unpark(testThread.thread);    }    public void testSleep() {        try {            Thread.sleep(2000L);        } catch (InterruptedException e) {            e.printStackTrace();        }        thread.start();        thread.interrupt();        Thread.yield();    }    public void testJoin() {        try {            thread.join();        } catch (InterruptedException e) {            e.printStackTrace();        }    }}